function A_A = convert_chl_to_algae(chl, T, I)% convert_chl_to_algae: converts chl concentration to algae%   concentration using chl:C and C:algae ratios, Kooi 2017% chl: chlorophyll-a concentration (mg chl m^-3)% T: water temperature (Celsius)% I: light intensity (micro mol quanta m^-2 s^-1)% return: ambient algae concentration (algal cells m^-3)    chl_over_carbon = chl_to_carbon_ratio(T, I);    A_A = chl * 1./chl_over_carbon * 1/kooi_constants.carbon_per_algae;end