function collisions = get_algae_collisions (particle, S, T, chl, I)    % particle is the particle in question.    % S is the salinity at the particle (g kg^-1)    % T is the temperature at the particle (Celsius)    % chl is the ambient chlorophyll-a concentration at the particle (mg m^-3)    % I is the light intensity at the particle (microEinsteins)        % methodology from Kooi 2017, eq. 11, first term        kconst = kooi_constants();    k = kconst.k;    mu_sw = dynamic_viscosity_seawater(S, T);    r_tot = particle.r_tot();  % radius of particle/algae system    r_A = kconst.r_A;  % radius of individual algae    V_s = get_settling_velocity(particle);  % settling velocity of particle    gamma = kconst.gamma; % shear rate    theta_pl = particle.theta_pl();        D_pl = k * (T + 273.16) / (6*pi * mu_sw * r_tot);   % diffusivity of plastic particle    D_A = k * (T + 273.16) / (6*pi * mu_sw * r_A);      % diffusivity of algae cell    Beta_A_brownian = 4*pi * (D_pl + D_A) * (r_tot + r_A);  % brownian motion kernel rate       Beta_A_settling = 1/2 * pi * r_tot^2 * V_s;  % differentia settling kernel rate         Beta_A_shear = 1.3 * gamma * (r_tot + r_A)^3;  % advective shear kernel rate        Beta_A = Beta_A_brownian + Beta_A_settling + Beta_A_shear;        A_A = get_ambient_algae_concentration(chl, T, I);  %ambient algae concentration        collisions = Beta_A * A_A / theta_pl;
endfunction
