function collisions = get_algae_collisions (particle, S, T, chl)    % particle is the particle in question.    % S is the salinity at the particle    % T is the temperature at the particle    % chl is the surface chlorophyll concentration above the particle        % methodology from Kooi 2017, eq. 11, first term        k = 0; % physconst('Boltzmann');    mu_sw = dynamic_viscosity_seawater(S, T);    r_tot = particle.r_pl + particle.t_bf()        D_pl = k * (T + 273.16) / (6*pi * mu_sw * r_tot);    Beta_A_brownian = 4*pi * (D_pl + D_A) * (r_tot + r_A);    Beta_A = Beta_A_brownian + Beta_A_settling + Beta_A_shear;    collisions = Beta_A * A_A / theta_pl;
endfunction
