function dAdt get_algae_flux_for_particle(dt, particle, S, T, chl, I)    % particle: the particle to update    % S: the salinity at the particle's location (g kg^-1)    % T: the temperature at the particle's location (Celsius)    % chl: the ambient chlorophyll concentration at the particle's location (mg m^-3)    % I: the light intensity at the particle's location (microEinsteins)    dAdt = get_algae_collisions(particle, S, T, chl, I) + ...           get_algae_growth(particle, chl, I) + ...           get_algae_mortality(particle) + ...           get_algae_respiration(particle, T);
endfunction