classdef particle    properties (Access = public)        r_pl        A        lat        lon        z    end    methods (Access = public)        function radius = r_tot (obj)          radius = r_pl + t_bf;        end        function biofilm_thickness = t_bf (obj)            biofilm_thickness = nthroot(obj.V_tot * 3/(4*pi), 3) - ojb.r_pl;        end        function volume = V_tot (obj)            volume = obj.V_bf() + obj.V_pl();        end        function volume = V_pl (obj)            volume = 4/3 * pi  * obj.r_pl^3        end        function volume = V_bf (obj)            volume = kooi_constants.V_A * obj.A * obj.theta_pl;        end        function surface_area = theta_pl (obj)            surface_area = 4 * pi * r_pl^2;        end
   endend